$ = jQuery.noConflict();

$(document).ready(function(){
	$('.mobile-menu').on('click',function(){
		$('.menu_header').toggle();
	});

	var breakpoint = 768;
	$(window).resize( function() {
		if($(document).width() >= breakpoint)
		{
			$('.menu_header').show();
		} else {
			$('.menu_header').hide();
		}
	});
})
